// Add scrolling function to jQuery
jQuery.fn.scrollTo = function(elem) { 
    $(this).animate({
        scrollTop:  $(this).scrollTop() - $(this).offset().top + $(elem).offset().top 
    }, speed = 300); 
    return this; 
};

